import React, { useEffect, useState } from "react";
export default function Form(props) {
  const [patient, setPatient] = useState();
  const [doctor, setDoctor] = useState();
  const [appointment, setAppointment] = useState();
  const [diagnosis, setDiagnosis] = useState();
  const [surveyPage, setSurveyPage] = useState(0);

  useEffect(() => {
    if(props){
      setPatient(props.entry[0]); //a better way would be to search through for resourceType
      setDoctor(props.entry[1]);
      setAppointment(props.entry[2]);
      setDiagnosis(props.entry[3]);
      setSurveyPage(1);
    }
  })

  function getSurveyPage() {
    switch(surveyPage) {
      case(1):
        return(
        <div>
          <p>Hi {patient.resource.name[0].given[0]}, on a scale of 1-10, would you recommend Dr.{doctor.resource.name[0].family} to a friend or family member? 1 = Would not recommend, 10 = Would strongly recommend</p>
          <input type="number" id="rating" name="rating" min="1" max="10"></input>
        </div>
        );
      default:
        return "Loading...";
    }
  }
  return(
    <p>
      {getSurveyPage()}
    </p>
  )
}
